<?phpnamespace Controller;include_once '../../Config/DbConnection.php';class adminLogin extends \DbConnection{ public function LoginAdmin($username,$password) {     $Query = 'select * from admin where username = ?';     $stmt = $this->Connect()->prepare($Query);     $stmt->bind_param('s',$username);     $stmt->execute();     $result = $stmt->get_result();     $teacher = $result->fetch_assoc();     if ($teacher && password_verify($password,$teacher['password'])){         session_start();         $_SESSION['admin'] = $teacher['username'];         echo json_encode(['success' => true,'message' => 'Successfully Logged']);     }else{         echo json_encode(['success' => false, 'message' => 'Invalid Password or Username']);     } }}if ($_SERVER['REQUEST_METHOD'] === 'POST'){    $userName = $_POST['userName'];    $password = $_POST['password'];    if (empty($userName) && empty($password)) {        echo json_encode(['success' => false, 'message' => 'Please enter your username and password to continue.']);    }else{        $Login = new adminLogin();        $Login->LoginAdmin($userName,$password);    }}