<?phpnamespace Controller;use Model\parentModel;include_once '../Model/parentModel.php';class controllerParent{    public function displayParent()    {        $model = new parentModel();        $parent = $model->displayParent();        $tr = '';        foreach ($parent as $parents){            $Name = $parents['FullName'];            $studentName = $parents['StudentName'];            $StudentNumber = $parents['StudentNumber'];            $Email_Or_username = $parents['Email_Or_username'];            $tr .=  '               <tr>                    <td>'.$Name.'</td>                    <td>'.$studentName.'</td>                    <td>'.$StudentNumber.'</td>                    <td>'.$Email_Or_username.'</td>               </tr>            ';        }        echo $tr;    }}