<?phpnamespace Model;include_once '../../Config/DbConnection.php';class announcementModel extends \DbConnection{  public function createAnnouncement($title,$dest,$imageName,$datePosted)  {      $query = "insert into announcement(title, fileName, descreption, datePosted)values(?,?,?,?)";      $stmt = $this->Connect()->prepare($query);      $stmt->bind_param('ssss',$title,$dest,$imageName,$datePosted);      if ($stmt->execute()){          echo json_encode(['success'  => true, 'message' => 'Announcement Successfully Created']);      }else{          echo json_encode(['success'  => false, 'message' => 'failed', 'error' => $stmt->error]);      }  }}