<?phpnamespace Model;include_once '../../Config/DbConnection.php';class parentModel extends \DbConnection{  public function displayParent()  {      $result = $this->Connect()->query('select FullName,StudentName,Email_Or_username,StudentNumber from parent');      $parent = [];      if ($result->num_rows > 0){          while ($row = $result->fetch_assoc()){              $parent[] = $row;          }          return $parent;      }      return null;  }}