ller\requirementController();    $requirement->displayRequirementList($studentId);    ?></section><div class="modal fade" id="RequirementModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">    <div class="modal-dialog modal-dialog-centered">        <form id="formUploadRequirement" action="../Controller/requirementController.php" enctype="multipart/form-data" method="post" class="modal-content">            <div class="modal-header">                <h1 class="modal-title fs-5" id="exampleModalLabel">Upload Requirement</h1>                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>            </div>            <div class="modal-body d-flex flex-column gap-2">                <label for="Name">                    Student Number                    <input name="studentNumber" value="<?= $_SESSION['StudentId']?>" type="text" class="form-control">                </label>                <label for="Name">                    Requirement Name                    <input name="RequirementName" type="text" class="form-control">                </label>                <label for="file">                    Upload Here                    <input name="fileUpload" accept="image/jpeg,image/png" type="file" class="form-control">                </label>                <div class="modal-footer">                    <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>                    <button type="submit" class="btn btn-primary">Upload</button>                </div>        </form>    </div></div><script>    $(document).ready(function (){        $(document).on('submit','#formUploadRequirement',function (event){            event.preventDefault();            const formData = new FormData(this)            formData.append('action','uploadRequirements')            $.ajax({                url: '../Controller/requirementController.php',                type: 'post',                data: formData,                contentType: false,                processData: false,                dataType: 'json',                success: function (res){                    if (res.error){                        Swal.fire({                            text: res.error.errorM,  // Display the error message                            icon: 'warning',                            iconColor: '#ff6347',                            toast: true,                            target: 'body',                            position: 'top-end',                            showConfirmButton: false,                            timer: 3000,  // Auto-close the alert after 2 seconds                            timerProgressBar: true,  // Show the progress bar for the timer                        });                    }                    if (res.success === true){                        Swal.fire({                            icon: "success",                            text: res.message,                            showConfirmButton: false,                            timer: 1500                        });                        setTimeout(() =>{window.location.reload()},2000)                    }                }            })        })        $(document).on('click','#delete_btn',function (e){            Swal.fire({                title: "Are you sure?",                text: "You won't be able to revert this!",                icon: "warning",                showCancelButton: true,                confirmButtonColor: "#3085d6",                cancelButtonColor: "#d33",                confirmButtonText: "Yes, delete it!"            }).then((result) => {                if (result.isConfirmed) {                    // ajax                    $.ajax({                        url: '../Controller/requirementController.php',                        type: 'post',                        data: {                            id: e.target.value,                            action: 'delete_'                        },                        dataType: 'json',                        success: function (res){                            if (res.success =  true){                                Swal.fire({                                    icon: "success",                                    text: res.message,                                    showConfirmButton: false,                                    timer: 1500                                });                                setTimeout(() =>{window.location.reload()},2000)                            }                        }                    })                }            });        })        $(document).on('click','#btn_update',function (e){            $.ajax({                url: '../Controller/requirementController.php',                type: 'post',                data: {                    id: e.target.value,                    action: 'update_'                },                    success: function (res) {                        if ($('#RequirementModalUpdate').length === 0) {                            $('body').append(res);                        } else {                            $('#RequirementModalUpdate').replaceWith(res)                        }                        $('#RequirementModalUpdate').modal('show')                    }            })        })        $(document).on('submit','#formUploadRequirementUpdate',function (event){            event.preventDefault();            const formData = new FormData(this)            formData.append('action','SaveRequirement')            $.ajax({                url: '../Controller/requirementController.php',                type: 'post',                dataType: 'json',                data: formData,                processData: false,                contentType: false,                success: function (res){                    if (res.success = true){                        Swal.fire({                            icon: "success",                            text: res.message,                            showConfirmButton: false,                            timer: 1500                        });                        setTimeout(() =>{window.location.reload()},2000)                    }else {                        Swal.fire({                            icon: "error",                            text: res.message,                            showConfirmButton: false,                            timer: 1500                        });                    }                }            })        })    })</script></body></html>